import logo from './logo.svg';
import './App.css';
import anime from 'animejs/lib/anime.es.js';
import {applyRouterMiddleware, BrowserRouter as Router, Routes, Route}
    from 'react-router-dom';
import {  Link } from "react-scroll";
import SubmitForm from './pages/SubmitForm'
import Project from './pages/Project'
import AboutMe from './pages/AboutMe'
import Home from './pages/Home'
import React, {useLayoutEffect, useRef, useState, useEffect } from 'react';
import { slide as Menu } from 'react-burger-menu';
import Sidebar from './Sidebar';
import styled from "styled-components";

const Div = styled.div`

  transform: translateX(${({ animate }) => (animate ? "0vw" : "-100vw")});
  transition: transform 1s ;

`;

function App() {
  const useDeviceDetect = () => {
    const [width, setWidth] = useState(window.innerWidth);
    const handleWindowResize = () => {
        setWidth(window.innerWidth);
    }

    useEffect(() => {
        window.addEventListener('resize', handleWindowResize);

        return () => {
            window.removeEventListener('resize', handleWindowResize);
        }
    }, []);

    return (width <= 768);
}
const [show, doShow] = useState({
  itemOne: false,
  itemTwo: false,
  itemThree: false
});
const refOne = useRef(null),
      refTwo = useRef(null),
      refThree = useRef(null);

useLayoutEffect(() => {
  //if(element=>element.getBoundingClientRect() === null){return;}
  console.log("reached");
  //const wrap = document.getElementById("body");
  //const topPos = wrap =>wrap.getBoundingClientRect().top;
  const topPos = element =>element.getBoundingClientRect().top;

  const div1Pos = topPos(refOne.current),
        div2Pos = topPos(refTwo.current),
        div3Pos = topPos(refThree.current);
  console.log(div1Pos);
  console.log(div2Pos);
  console.log(div3Pos);
  const onScroll = () => {
  const scrollPos = window.scrollY + window.innerHeight;
    console.log(scrollPos);
    if (div1Pos < scrollPos - 150) {
      console.log("*");
      doShow(state => ({ ...state, itemOne: true }));
    } else if (div2Pos < scrollPos) {
      doShow(state => ({ ...state, itemTwo: true }));
    } else if (div3Pos < scrollPos) {
      doShow(state => ({ ...state, itemThree: true }));
    }
  };

  window.addEventListener("scroll", onScroll);
  return () => window.removeEventListener("scroll", onScroll);
}, []);


  return (



        <div className="App" id="outer-container">

         {useDeviceDetect()?<Sidebar pageWrapId={'page-wrap'} outerContainerId={'outer-container'} />: <nav class="navbar">
          <ul>
            <li><a href="#home">.home</a></li>
            <li><a href="#about">.about</a></li>
            <li><a href="#projects">.projects</a></li>
            <li><a href="#contact">.contact</a></li>

          </ul>
        </nav>}

          <div id="page-wrap" >



           <section id="home">
              <Home />
               </section>





          <Div  animate={show.itemThree} ref={refThree}>
          <section id="about" >
             <AboutMe />
              </section>
          </Div>



         <Div  animate={show.itemTwo} ref={refTwo}>
         <section id="projects">
            <Project />
            </section>
         </Div>

          <Div  animate={show.itemOne} ref={refOne} >
          <section id="contact">
            <SubmitForm />  
          </section>
         </Div>



          </div>

        </div>



  );

}


export default App;
